import Enum from 'enum'

var types = new Enum(['MATCH', 'CONTAINS', 'FUNCTION'])

export default class Tag {
  constructor(name, type) {
    this.name = name
    this.type = type
  }

  withFunction(func) {
    this.func = func
    return this
  }

  static types = types

  isMatch = () => Tag.types.MATCH.is(this.type)
  isContains = () => Tag.types.CONTAINS.is(this.type)
  isFunction = () => Tag.types.FUNCTION.is(this.type)
}
