import Tag from '../../Tag'

const higher = (element, key) => (name, term) => {
  return [element, key, name, term]
}

export default (element, key) => {
  let value = element[key]

  return [
    new Tag(value.toString(), Tag.types.MATCH.key),
    new Tag(key + '=' + value, Tag.types.MATCH.key),
    new Tag(key + '>', Tag.types.FUNCTION.key).withFunction(higher(element, key))
  ]
}