import assert from 'assert'

const errors = {
  FILTER_SIZE: 'Wrong number of itens returned',
  ITEM_DIFFERENT: 'The item is different'
}

export default (list, indexes, result) => {
  assert.equal(indexes.length, result.length, errors.FILTER_SIZE)

  indexes.forEach((index, i) => {
    assert.deepEqual(list[index], result[i], errors.ITEM_DIFFERENT)
  })
}