import assert from 'assert'

const errors = {
  TAG_SIZE: 'Wrong number of tags created',
  TAG_NAME: 'Wrong name of tag',
  TAG_TYPE: 'Wrong type of tag'
}

export default (expected, results) => {
  assert.equal(expected.length, results.length, errors.TAG_SIZE)

  expected.forEach((expect, i) => {
    assert.equal(expect.name, results[i].name, errors.TAG_NAME)
    assert.equal(expect.type, results[i].type, errors.TAG_TYPE)
  })
}