import assert from 'assert'
import validateTag from './validateTag'

const errors = {
  INDEX_SIZE: 'Different number of index tagged',
  INDEX_TAG: 'Tags not created at index ',
  TAG_TYPE: 'Wrong type of tag'
}

export default (expected, results) => {
  const expectedLength = Object.keys(expected).length
  const resultsLength = Object.keys(results).length
  assert.equal(expectedLength, resultsLength, errors.INDEX_SIZE)

  Object.keys(expected).forEach(key => {
    assert.notEqual(null, results[key], errors.INDEX_TAG + key)
    validateTag(expected[key], results[key])
  })
}