import validateTag from '../_helper/validateTag'
import builderBoolean from '../../src/utils/tagBuilder/builder.boolean'

describe('builder.boolean', () => {
  it('should create false tag', () => {
    const expected = [{ name: '!dead', type: 'MATCH' }]
    const results = builderBoolean({ dead: false }, 'dead')

    validateTag(expected, results)
  })

  it('should create true tag', () => {
    const expected = [{ name: 'champion', type: 'MATCH' }]
    const results = builderBoolean({ champion: true }, 'champion')

    validateTag(expected, results)
  })
})