import validateTag from '../_helper/validateTag'
import builderNumber from '../../src/utils/tagBuilder/builder.number'

describe('builder.number', () => {
  it('should create number tag', () => {
    const expected = [
      { name: '500', type: 'MATCH' },
      { name: 'size=500', type: 'MATCH' },
      { name: 'size>', type: 'FUNCTION' }
    ]
    const results = builderNumber({ size: 500 }, 'size')

    validateTag(expected, results)
  })
})